#include <HardwareSerial.h>

HardwareSerial mySerial(1); // use UART1

// Pin assignments 
#define RX_PIN   9
#define TX_PIN   10
#define RTS_PIN  7
#define CTS_PIN  8

void setup() {
  Serial.begin(115200);

  // Start UART1 with flow control
  mySerial.begin(115200, SERIAL_8N1, RX_PIN, TX_PIN, true, RTS_PIN, CTS_PIN);

  Serial.println("ESP32-S3 UART (with RTS/CTS) Ready");
}

void loop() {
  // Send test data
  String message = "Sensor packet: IMU_AX=0.12\n";
  mySerial.print(message);

  // Listen for response
  if (mySerial.available()) {
    String response = mySerial.readStringUntil('\n');
    Serial.println("Got from MCU: " + response);
  }

  delay(1000);
}
