#include <HardwareSerial.h>

HardwareSerial mySerial(1); // use UART1

// Pin assignments 
#define RX_PIN   9
#define TX_PIN   10
#define RTS_PIN  7
#define CTS_PIN  8

void setup() {
  Serial.begin(115200);

  // Start UART1 with flow control
  mySerial.begin(115200, SERIAL_8N1, RX_PIN, TX_PIN, true, RTS_PIN, CTS_PIN);

  Serial.println("ESP32-S3 UART (with RTS/CTS) Ready");
}

void loop() {
  // Send test data
  String message = "Sensor packet: IMU_AX=0.12\n";
  mySerial.print(message);

  // Listen for response
  if (mySerial.available()) {
    String response = mySerial.readStringUntil('\n');
    Serial.println("Got from MCU: " + response);
  }

  delay(1000);
}

// Penny ESP32 - Blink Example
// Demonstrates basic functionality on ultra-compact board

#define LED_PIN 2
#define BUTTON_PIN 0

void setup() {
  Serial.begin(115200);
  pinMode(LED_PIN, OUTPUT);
  pinMode(BUTTON_PIN, INPUT_PULLUP);

  Serial.println("Penny ESP32 - Ultra Compact Board Ready!");
  Serial.printf("Board Size: ~19mm diameter\n");
}

void loop() {
  // Simple blink with button control
  if (digitalRead(BUTTON_PIN) == LOW) {
    digitalWrite(LED_PIN, HIGH);
    Serial.println("LED ON - Button Pressed");
    delay(100);
  } else {
    digitalWrite(LED_PIN, LOW);
    delay(500);
  }

  // Demonstrate WiFi capability
  static unsigned long lastWiFiCheck = 0;
  if (millis() - lastWiFiCheck > 5000) {
    Serial.printf("WiFi Status: %s\n", WiFi.status() == WL_CONNECTED ? "Connected" : "Disconnected");
    lastWiFiCheck = millis();
  }
}
